namespace NagadApi\lib;


use Dotenv\Dotenv;

/**
 * Class Key
 * @package NagadApi\lib
 */
class Key
{
    private $merchantPrivateKey = "";
    private $pgPublicKey = "";
    private $timeZone = '';
    private $appEnv = '';
    private $merchantID = '';

    /**
     * @param string $pgPublicKey
     */
    public function setPgPublicKey($pgPublicKey)
    {
        $this->pgPublicKey = $pgPublicKey;
    }


    /**
     * Key constructor.
     * @param string $merchantPrivateKey
     * @param $pgPublicKey
     * @param $timeZone
     * @param $merchantID
     * @param $appEnv
     */
    public function __construct($merchantPrivateKey, $pgPublicKey, $timeZone, $merchantID, $appEnv)
    {
        //TODO:: need to dynamic .env data here
        $this->merchantPrivateKey = $merchantPrivateKey;
        $this->pgPublicKey = $pgPublicKey;
        $this->timeZone = $timeZone;
        $this->merchantID = $merchantID;
        $this->appEnv = $appEnv;
    }


    /**
     * @return string
     */
    public function getAppEnv()
    {
        return $this->appEnv;
    }

    /**
     * @return string
     */
    public function getTimeZone()
    {
        return $this->timeZone;
    }

    /**
     * @return string
     */
    public function getMerchantID()
    {
        return $this->merchantID;
    }

    /**
     * @param string $merchantPrivateKey
     */
    public function setMerchantPrivateKey($merchantPrivateKey)
    {
        $this->merchantPrivateKey = $merchantPrivateKey;
    }

    /**
     * @return mixed
     */
    protected function getMerchantPrivateKey()
    {
        return $this->merchantPrivateKey;
    }

    /**
     * @return mixed
     */
    protected function getPgPublicKey()
    {
        return $this->pgPublicKey;
    }

    /**
     * @param string $timeZone
     */
    public function setTimeZone($timeZone)
    {
        $this->timeZone = $timeZone;
    }

    /**
     * @param string $appEnv
     */
    public function setAppEnv($appEnv)
    {
        $this->appEnv = $appEnv;
    }

    /**
     * @param string $merchantID
     */
    public function setMerchantID($merchantID)
    {
        $this->merchantID = $merchantID;
    }


    public static function generateEnv()
    {
        //TODO:: error here

        define('ABSPATH', __DIR__ . '/../../');

        $dotenv = Dotenv::createImmutable(ABSPATH);
        $dotenv->load();
    }


}